mapper.select:
/*getPopupList*/ SELECT BANNER_POPUP_UUID ,BANNER_POPUP_GUBUN ,BANNER_POPUP_WIDTH ,BANNER_POPUP_HEIGHT ,BANNER_POPUP_TOP ,BANNER_POPUP_LEFT ,BANNER_POPUP_START_DT ,BANNER_POPUP_END_DT ,BANNER_POPUP_TITLE ,USE_AT FROM REHMAUC.BANNER_POPUP WHERE ACTIVE = 'Y'
AND BANNER_POPUP_TITLE LIKE CONCAT('%', #{searchInput}, '%')
ORDER BY BANNER_POPUP_UUID DESC
LIMIT #{pagination.limitStart}, #{recordSize}

mapper.insert:
/*setBannerPopupInsert */
INSERT INTO REHMAUC.BANNER_POPUP (BANNER_POPUP_GROUP, BANNER_POPUP_GUBUN, BANNER_POPUP_WIDTH, BANNER_POPUP_HEIGHT, BANNER_POPUP_TOP, BANNER_POPUP_LEFT, BANNER_POPUP_LINK, BANNER_POPUP_TARGET, BANNER_POPUP_START_DT, BANNER_POPUP_END_DT, BANNER_POPUP_TITLE, REGIST_DT, REGIST_ID, USE_AT) VALUES (#{bannerPopupGroup}, #{bannerPopupGubun}, #{bannerPopupWidth}, #{bannerPopupHeight}, #{bannerPopupTop}, #{bannerPopupLeft}, #{bannerPopupLink}, #{bannerPopupTarget}, #{bannerPopupStartDt}, #{bannerPopupEndDt}, #{bannerPopupTitle}, NOW(), #{id}, #{useAt})
SELECT AUTO_INCREMENT FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_SCHEMA = 'rehmauc' AND TABLE_NAME = 'BANNER_POPUP'

mapper.update:
/*setBannerPopupUpdate*/
UPDATE
    REHMAUC.BANNER_POPUP
SET
    BANNER_POPUP_GROUP = #{bannerPopupGroup}
    ,BANNER_POPUP_GUBUN = #{bannerPopupGubun}
    ,BANNER_POPUP_WIDTH = #{bannerPopupWidth}
    ,BANNER_POPUP_HEIGHT = #{bannerPopupHeight}
    ,BANNER_POPUP_TOP = #{bannerPopupTop}
    ,BANNER_POPUP_LEFT = #{bannerPopupLeft}
    ,BANNER_POPUP_LINK = #{bannerPopupLink}
    ,BANNER_POPUP_TARGET = #{bannerPopupTarget}
    ,BANNER_POPUP_START_DT = #{bannerPopupStartDt}
    ,BANNER_POPUP_END_DT = #{bannerPopupEndDt}
    ,BANNER_POPUP_TITLE = #{bannerPopupTitle}
    ,MODIFY_DT = NOW()
    ,MODIFY_ID = #{id}
    ,USE_AT = #{useAt}
WHERE
    BANNER_POPUP_UUID = #{uuid}

/*setBannerPopupDelete*/
UPDATE REHMAUC.BANNER_POPUP
SET ACTIVE = 'N'
WHERE BANNER_POPUP_UUID = #{uuid}
